INSERT INTO `movies` VALUES (170983, 1042834, '伊甸', 'Eden', '　　一座资源紧缺的无人岛，三组各怀心思的求生者，毒害、背叛、爆头... 荒岛硬核大逃杀，残酷社达动物园！当粮食枯竭、水源污染、信任崩盘，生存成了唯一的目标。为了活下去，人性被兽性替代，欲望滋生出邪恶，而谁又将成功活到最后……', '2025-04-03', 6.992, 186, 21.0429, '/nJ3ZvkedqkYHWZQbOnuheiEhytE.jpg', '/rz8NcfTRczvn94vpnmMx42zt6EC.jpg', 0, 0, 'en', '[53, 18]', 0);
INSERT INTO `movies` VALUES (170984, 862, '玩具总动员', 'Toy Story', '胡迪是小主人安迪最喜欢的传统牛仔玩偶，他和其他玩具趁主人不在时，便会＂活＂起来一起玩闹。可是好景不常，最新奇的热门玩具巴斯光年来了，他让胡迪备受冷落。失宠的胡迪为了巩固自己的地位，只好处心积虑地想要赶走巴斯。在一次意外中，胡迪和巴斯不幸陷入一个玩具虐待狂的邻居家中而命在旦夕。两个冤家路窄互不相容的对手，是否能够化敌为友、发挥机智，顺利地通过这场冒险之旅，回到小主的身边呢？', '1995-11-22', 7.969, 19066, 22.5845, '/zOpj9XcRHKiHzS3z9I7NdTFX48O.jpg', '/3Rfvhy1Nl6sSGJwyjb0QiZzZYlB.jpg', 0, 0, 'en', '[16, 12, 10751, 35, 878]', 0);
