INSERT INTO `movies` VALUES (171030, 1211726, '红丝绸', 'Красный шелк', '1927年，一列横跨西伯利亚的特快列车正秘密运送着关乎苏联与中国命运的重要文件。在这趟列车上，看似普通的乘客中暗藏杀机——外国间谍与亡命之徒为夺取文件不择手段。当危机降临，一位年轻的红军战士不得不与曾经的沙皇密探联手，在飞驰的钢铁牢笼中揪出幕后黑手。', '2025-02-20', 7.273, 11, 3.772, '/yveeiLDhI1fsA1zfLNLyX1pmao1.jpg', '/lH1FDMWWb1t3KneoRQ6O5r8q9do.jpg', 0, 0, 'ru', '[28, 12, 9648]', 0);
INSERT INTO `movies` VALUES (171031, 556574, '汉密尔顿', 'Hamilton', '这部音乐剧严格遵照历史，讲述 Hamilton 政治生涯中的几件大事，他的战友和政敌、他与妻子 Eliza 和长子 Philip 的深厚感情以及他犯下的错误。历史人物 George Washington、Thomas Jefferson & James Madison 等在剧中轮流出场。剧中音乐以Hip-hop为主，穿插了Jazz，R&B, Tin Pan Alley 等多种风格的歌曲。', '2025-09-05', 0, 0, 4.8256, '/h1B7tW0t399VDjAcWJh8m87469b.jpg', '/uWVkEo9PWHu9algZsiLPi6sRU64.jpg', 0, 0, 'en', '[36, 18]', 0);
INSERT INTO `movies` VALUES (171032, 1357759, '女孩', '女孩', '　　1988 年，基隆港烟尘蔽日，林小丽（白小樱 饰）在迷惘中长大，渴望逃离黑暗。 直到遇见无惧眼光、自在生活的李莉莉（林品彤 饰），她才第一次看见世界的色彩。 然而，这份向往却触动了母亲阿娟的过往创伤。 当年，她也曾憧憬未来，却被现实困住。 女孩与女人，在交错的命运中互相映照，像一面镜子照出彼此的伤却无能为力。', '2025-09-03', 0, 0, 4.3288, '/6oQKPnyOFL2YdhPyhiyGUKIDfTI.jpg', '/b8VE0s93kX4XZRTzVVnjmGipiSm.jpg', 0, 0, 'zh', '[18]', 0);
