INSERT INTO `movies` VALUES (171039, 1523345, '营救飞虎', '营救飞虎', '援华美军飞虎队飞行员詹姆斯（Mitchell Hoog 饰），在执行任务时被日军击落。东江纵队游击队长单刀（伍允龙 饰）、沙胆仔（陈永胜 饰）、三家姐（王丹妮 饰）及虾米（韩陌 饰）前往营救，一句“我们带你回家”尽显勇敢与担当。与此同时，我党潜伏人员欢少（韩庚 饰）在机缘巧合下与救援队伍达成合作。6个人，48小时，他们将如何扭转战局？又能否创造营救奇迹？', '2025-09-03', 0, 0, 4.762, '/vRB4nmb7h8HB82XKIWf79q75V6u.jpg', '/cgWjxtLd2wKHUKmi0Fmr5K9NN0x.jpg', 0, 0, 'zh', '[18, 10752]', 0);
