INSERT INTO `movies` VALUES (171041, 1280330, '蛟龙行动', '蛟龙行动', '　　未来某日，中国海军截获关键情报——一艘超级潜艇正密谋一项针对我国东南沿海城市的绝密军事计划。我方派出最新型潜艇“龙鲸号”，与蛟龙小队协同作战。海底战场险象环生，蛟龙遭遇重重陷阱，潜艇与敌多番埋伏周旋，更驶入海底火山口遍布的广袤深海，一场惊心动魄、攸关生死存亡的深海决战拉开帷幕……\r 　　该影片为《红海行动》续作。', '2025-01-26', 8.6, 5, 4.8246, '/uBrKUkHqXXRzmYlGr87HyYGeiQl.jpg', '/wteoGPQwhj79yQuEINoTwqfEzav.jpg', 0, 0, 'zh', '[18, 28, 10752]', 0);
