INSERT INTO `movies` VALUES (171049, 1092506, '快马驰情', 'On Swift Horses', '新婚夫妇穆里尔和李在后者从朝鲜战争回来后开始了光明的新生活，然而不久后，新建立的稳定状态被李魅力十足的弟弟朱利叶斯的到来颠覆，他是一个有秘密、任性的赌徒，一段危险的三角恋很快形成，三人立誓要一起在加州开始新生活。好景不长，随着朱利叶斯突然消失去到拉斯维加斯，他们对美国梦的追求破灭，穆里尔开始了她自己的秘密生活，进行赛马赌博，并发现了她从未想过的爱情。', '2025-04-24', 6.111, 54, 4.1275, '/qmLYrIUTcWjDSGEuCtR3clvpgv0.jpg', '/jKMbP0rwEkJYxkNRFQXFkVIIzGP.jpg', 0, 0, 'en', '[18, 10749]', 0);
