INSERT INTO `movies` VALUES (171087, 1135176, '家庭简史', '家庭简史', '影片讲述了操场上的一次伤人事故让沉默寡言但善于观察的严硕和热情直率却有些任性的涂伟成为了朋友。随着友谊的深化，严硕认识了涂伟的父母，他们对严硕这个失去了母亲并遭酗酒父亲虐待的孩子由同情变成喜爱。严硕成为了涂伟家的常客，而他的父亲对他的控制也更加紧密。某天深夜，涂伟一家收到严硕父亲意外死亡的消息，他们如何应对？严父的死又是否像严所说的只是个意外。四个人的命运交织了起来。', '2024-10-15', 5.967, 15, 2.6442, '/7MjB0A7ciykouc2s5iHejUyz48D.jpg', '/gJDGWlJbFO4KqB47ng3d0skA6zq.jpg', 0, 0, 'zh', '[18, 9648, 53]', 0);
