INSERT INTO `movies` VALUES (171122, 315465, '怪物之子', 'バケモノの子', '《怪物之子》的世界里，在人类世界之外还有一个怪物生活的世界。而两者之间的交界处则是人间界的涉谷所在以及在怪物界名为“涉天街”的地方。故事讲述了某一日，孤身一人的少年误入怪物世界的少年遇到了同样孤身一人的怪物，两者相遇后开展的超出想象的冒险故事。少年成为了名为熊彻的怪物的弟子，同时获得了“九太”这个名字。也因此有网友笑言，应该给本片命名为“熊孩子”。', '2015-07-11', 7.943, 1529, 2.7429, '/rtWJz30OsShe9mZLbkAk7QKgnvX.jpg', '/8yniaG7C4iCm0VVTSShAPUZYM3Y.jpg', 0, 0, 'ja', '[28, 12, 16, 18, 10751, 14]', 0);
