INSERT INTO `movies` VALUES (171133, 1151272, '接近终点', 'Sirāt', '父亲Sergi López带着儿子深入摩洛哥南部山区，闯入一场锐舞派对寻找数月前在此类永不停歇的迷幻狂欢中失踪的女儿兼妹妹Mar。置身于电子音乐轰鸣与陌生而原始的自由氛围中，他们不断向人递出Mar的照片。希望日渐渺茫，却仍追随一群锐舞者奔赴沙漠中的终极派对。当父子俩深入灼热荒原，这场追寻之旅终将迫使他们直面自身的局限。', '2025-06-06', 6.9, 17, 2.6923, '/mCunZGtfNoyoV21GmwIOxefM8kM.jpg', '/kOUV5WbisWMg56d8eB45ZHEVxYq.jpg', 0, 0, 'es', '[18, 10402]', 0);
