INSERT INTO `movies` VALUES (171151, 1244112, '糕点铺精灵2', 'Die Heinzels: Neue Mützen, Neue Mission', '小精灵小飞向往希望打破「默默助人」的传统，这样的想法让保守的族人们大感不满，某天，小飞遇见来自另一个精灵族的小波，小波的族群热爱刺激，善用高科技道具做大胆的事，这也让他们成了警探兰丝琪和警猫丸子的眼中钉，小飞与小波的友谊，能否化解两个族群长达250年的恩怨，并让她实现梦想呢？', '2024-12-24', 6.1, 7, 2.2619, '/nlFft5OJBQ2sDjGsmT1xO5NK8zC.jpg', '/61arVnYIN3OefoZkVXcAPq2mJ2N.jpg', 0, 0, 'de', '[16, 12, 10751]', 0);
