INSERT INTO `movies` VALUES (171190, 1392527, '雷诺阿', 'ルノワール', '影片用细腻的笔触描绘了11岁少女在1987年的夏天发生的故事，忙于工作的母亲，生病住院的父亲。处在孩子和大人之间淡淡的分界线上飘荡的少女，一边窥视着大人的世界，一边感受到人们内心的痛苦。对死亡的好奇心和恐惧，活着是无可奈何的寂寞，渴求被人温暖。高兴、 开心、寂寞、恐惧、哀伤，从少女的视角浮现出各种各样的情感的闪光。', '2025-06-20', 6, 1, 2.0795, '/l8SW5j7bQbw7QRUKQA3wK8CDdJv.jpg', '/pq2giNpWsJWmwF9u8gRNU2Bzt76.jpg', 0, 0, 'ja', '[18]', 0);
