INSERT INTO `movies` VALUES (171233, 1476988, '普通的孩子', 'ふつうの子ども', '本片的主人公是上田唯士（岛田铁太 饰），10岁，小学四年级。他是个普通的男孩，喜欢小动物。然而，这个“普通的男孩”恋爱了！他的心动对象是三宅心爱（瑠璃 饰）——一位热衷环保的“环保少女”。为了接近心爱，唯士决定加入她热衷的环保活动。与此同时，班上的小小问题儿童桥本阳斗（味元耀大 饰）也意外加入。就这样，三人开启了一场环保行动，然而事情的发展却远远超出了他们的想象…', '2025-09-05', 0, 0, 1.7272, '/sOTbmTLnKICp6tr9x7fjfyohozC.jpg', '/mZnvOFuyxilcUIEHxFlNeJSuQdD.jpg', 0, 0, 'ja', '[18, 35]', 0);
