INSERT INTO `movies` VALUES (171283, 1220564, '密探', 'O Agente Secreto', '　　1977年，在被军事独裁统治折磨的巴西，四十多岁的马塞洛逃离了他混乱的过去，来到累西腓，希望在这里建立新的生活并与家人重修旧好。然而，他并未料到死亡威胁如影随形，笼罩在他的头顶。', '2025-07-23', 8.5, 2, 2.1106, '/ny3jNGDfvFVBlQHknhE9pr3SRH1.jpg', '/pKwwcqT5wI9Uc0Gnp9EiNmBWbM5.jpg', 0, 0, 'pt', '[18, 53, 36]', 0);
