INSERT INTO `movies` VALUES (171285, 1225624, '坠落', 'On Falling', '《坠落》讲述了一位在苏格兰一个庞大的物流中心工作的葡萄牙仓库搬运工奥罗拉的故事。她被困在工作场所的狭小空间和公寓的孤独之中，试图抵抗孤独、疏离以及随之而来的闲聊，这些都开始威胁到她的自我认知。在这种由算法驱动的零工经济的背景下，这种经济旨在将我们分开，《坠落》探讨了人们在沉默中为寻找意义和联系而进行的顽强斗争。', '2025-03-07', 6.7, 12, 1.1247, '/8mmCe3IVdppWqcoGaY4udZvdXHI.jpg', '/ki080Sbbqn5o0ypAPpRinROs0ZI.jpg', 0, 0, 'en', '[18]', 0);
