INSERT INTO `movies` VALUES (171377, 988361, '牧羊人', 'Bergers', '马蒂亚斯是一位怀揣文学梦想的年轻人，他离开蒙特利尔的广告行业，前往法国南部成为了一名牧羊人。然而，通往他向往的田园宁静之路却充满艰辛。伊莉丝是一位毅然辞职的公务员，她的来访为马蒂亚斯的探索之旅带来了新的方向。两人携手踏上迁徙之旅，在山区的挑战和对羊群的照料中，共同开创属于自己的新生活。', '2024-11-15', 7.167, 27, 0.8961, '/WTr2CdJhvhS3IOUdkaGHbIiieo.jpg', '/oerIAQCV2GjF4fdYZB8bhbwgQnU.jpg', 0, 0, 'fr', '[18]', 0);
