INSERT INTO `movies` VALUES (171902, 1035090, '维梅尔：光影大师', 'Vermeer: The Greatest Exhibition', '2023年春季，阿姆斯特丹国家博物馆呈现有史以来规模最大的维梅尔展览。维梅尔用色独特，爱用群青蓝等昂贵颜料，擅长捕捉室内空间的光与影，以物件与人物的互动呈现岁月静好的人文气氛。观众能以电影形式享受这个一票难求的展览，并由阿姆斯特丹国家博物馆馆长和策展人亲自导赏，展览云集全球不同博物馆藏品，维梅尔35件已知作品中的28件稀世名画，包括著名的《戴珍珠耳环的少女》、《地理学家》、《倒牛奶的女仆》及《窗边读信的少女》等。阿姆斯特丹国家博物馆和海牙莫瑞泰斯皇家美术馆利用现代科技，对维梅尔的创作灵感和绘画过程进行深入研究。观众可在大银幕仔细欣赏维梅尔巨作的细节，并在专业鉴赏下深入了解其生平及创作内幕，沉浸在维梅尔的艺术世界中！', '2023-04-18', 7.8, 6, 0.2822, '/c5yFZLWeimRjzPDepmEGhGgsgQG.jpg', '/18c2tpFA1rF51Tg7Mk5BKNUC7ow.jpg', 0, 0, 'en', '[99]', 0);
