INSERT INTO `upcoming` VALUES (0, '/w3RDV3pSpxN0C2DZ4Xpw4o5LWpI.jpg', '[35, 12]', 649, 'en', 'The Phoenician Scheme', '欧洲军火与航空界的商业巨擘Zsa-zsa Korda（本尼西奥·德尔·托罗 Benicio del Toro 饰）为了执行神秘计划，指定自己的修女女儿Liesl（米娅·塞普雷顿 米娅·瑟普尔顿 饰）成为遗产的继承人，而另一群匪徒想要阻止这位大亨，混乱与杀戮就此展开。', 14.7125, '/sFi7RHTc3baNAV81FJZgPojmgHo.jpg', '2025-05-23 00:00:00', '腓尼基计划', 0, 6.72, 522, '2025-09-10 00:00:00', '2025-10-01 00:00:00');
