INSERT INTO `upcoming` VALUES (0, '/vuglA60RqvpHK9rIcG8sXaiWw1L.jpg', '[18, 35, 10749]', 662, 'cn', '重慶森林', '编号为223的警察失恋后患上失恋综合症，在与金发女杀手擦肩而过又离奇相遇并有了一晚温情后，原本以为包括“爱情”在内的所有东西都有保质期的他意外地迎来心灵的短暂温暖。可是，他们的爱情还是结束了。快餐店新来的女招待阿菲爱上了时常光顾快餐店的编号为633的警察，因拆了他的女友留在快餐店给他的“分手” 信，阿菲知晓了他的心情，偷拿到他的钥匙趁他不在时常潜入他家，一边梦游一边悄悄地改变他的生活，终在被他撞见时令其感受到情感的回归。然而，这才是他们爱情的开始。', 8.5628, '/ejkczWHjQoNAPUmTU26tU3aRkr8.jpg', '1994-07-14 00:00:00', '重庆森林', 0, 8, 1997, '2025-09-10 00:00:00', '2025-10-01 00:00:00');
