INSERT INTO `upcoming` VALUES (0, '/5Jn2wgZ6EGu7ii9oPXgneSth5nR.jpg', '[18, 10752, 36]', 681, 'zh', '南京照相馆', '影片故事取材于南京大屠杀期间日军真实罪证影像。一群生活在南京的百姓躲在吉祥照相馆中避难，为了尽可能的多活一日，他们被迫帮助日军摄影师冲洗底片，却意外冲印出了能证明日军屠城的罪证照片。他们原本只想在大屠杀中保命活下去，面对日军在南京城内的暴行，他们决定让这些底片留存下去……', 6.2001, '/lwrANgpPEBmYZ59oExpCiuv34Ou.jpg', '2025-07-19 00:00:00', '南京照相馆', 0, 8.5, 23, '2025-09-10 00:00:00', '2025-10-01 00:00:00');
